@@ -0,0 +1,182 @@
+import { useState } from 'react';
+import { User, Mail, Phone, MapPin, Shield, AlertTriangle, Eye, EyeOff } from 'lucide-react';
+
+export default function AddUsers() {
+  const [formData, setFormData] = useState({
+    firstName: '',
+    lastName: '',
+    email: '',
+    phone: '',
+    alternatePhone: '',
+    dateOfBirth: '',
+    gender: '',
+    address: '',
+    city: '',
+    county: '',
+    emergencyContact: '',
+    emergencyPhone: '',
+    userType: '',
+    organizationName: '',
+    professionalId: '',
+    specialization: '',
+    password: '',
+    confirmPassword: '',
+    agreeToTerms: false,
+    consentToDataProcessing: false
+  });
+
+  const [showPassword, setShowPassword] = useState(false);
+  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
+  const [errors, setErrors] = useState({});
+  const [isSubmitting, setIsSubmitting] = useState(false);
+
+  const handleInputChange = (e) => {
+    const { name, value, type, checked } = e.target;
+    setFormData(prev => ({
+      ...prev,
+      [name]: type === 'checkbox' ? checked : value
+    }));
+
+    if (errors[name]) {
+      setErrors(prev => ({
+        ...prev,
+        [name]: ''
+      }));
+    }
+  };
+
+  const validateForm = () => {
+    const newErrors = {};
+    if (!formData.firstName.trim()) newErrors.firstName = 'First name is required';
+    if (!formData.lastName.trim()) newErrors.lastName = 'Last name is required';
+    if (!formData.email.trim()) newErrors.email = 'Email is required';
+    if (!formData.phone.trim()) newErrors.phone = 'Phone number is required';
+    if (!formData.userType) newErrors.userType = 'User type is required';
+    if (!formData.password) newErrors.password = 'Password is required';
+    if (!formData.confirmPassword) newErrors.confirmPassword = 'Please confirm password';
+
+    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
+    if (formData.email && !emailRegex.test(formData.email)) {
+      newErrors.email = 'Please enter a valid email address';
+    }
+
+    const phoneRegex = /^[+]?[\d\s\-()]+$/;
+    if (formData.phone && !phoneRegex.test(formData.phone)) {
+      newErrors.phone = 'Please enter a valid phone number';
+    }
+
+    if (formData.password && formData.password.length < 8) {
+      newErrors.password = 'Password must be at least 8 characters';
+    }
+
+    if (formData.password !== formData.confirmPassword) {
+      newErrors.confirmPassword = 'Passwords do not match';
+    }
+
+    if (formData.userType === 'service_provider') {
+      if (!formData.organizationName.trim()) {
+        newErrors.organizationName = 'Organization name is required for service providers';
+      }
+      if (!formData.professionalId.trim()) {
+        newErrors.professionalId = 'Professional ID is required for service providers';
+      }
+    }
+
+    if (!formData.agreeToTerms) {
+      newErrors.agreeToTerms = 'You must agree to the terms and conditions';
+    }
+    if (!formData.consentToDataProcessing) {
+      newErrors.consentToDataProcessing = 'Consent to data processing is required';
+    }
+
+    setErrors(newErrors);
+    return Object.keys(newErrors).length === 0;
+  };
+
+  const handleSubmit = async (e) => {
+    e.preventDefault();
+    if (!validateForm()) return;
+
+    setIsSubmitting(true);
+    try {
+      await new Promise(resolve => setTimeout(resolve, 2000));
+      console.log('Form submitted:', formData);
+
+      setFormData({
+        firstName: '',
+        lastName: '',
+        email: '',
+        phone: '',
+        alternatePhone: '',
+        dateOfBirth: '',
+        gender: '',
+        address: '',
+        city: '',
+        county: '',
+        emergencyContact: '',
+        emergencyPhone: '',
+        userType: '',
+        organizationName: '',
+        professionalId: '',
+        specialization: '',
+        password: '',
+        confirmPassword: '',
+        agreeToTerms: false,
+        consentToDataProcessing: false
+      });
+
+      alert('User registration successful!');
+    } catch (error) {
+      console.error('Registration error:', error);
+      alert('Registration failed. Please try again.');
+    } finally {
+      setIsSubmitting(false);
+    }
+  };
+
+  return (
+    <div className="min-h-screen bg-gradient-to-br from-purple-50 to-pink-50 py-8 px-4">
+      <div className="max-w-4xl mx-auto">
+        <div className="bg-white rounded-2xl shadow-xl overflow-hidden">
+          {/* Header */}
+          <div className="bg-gradient-to-r from-purple-600 to-pink-600 px-8 py-6">
+            <div className="flex items-center space-x-3">
+              <Shield className="h-8 w-8 text-white" />
+              <div>
+                <h1 className="text-2xl font-bold text-white">GBV Platform Registration</h1>
+                <p className="text-purple-100 mt-1">Join our safe and supportive community</p>
+              </div>
+            </div>
+          </div>
+
+          {/* Form wrapper added here ✅ */}
+          <form onSubmit={handleSubmit} className="px-8 py-6">
+            {/* ... all your input fields ... */}
+
+            <div className="flex justify-center">
+              <button
+                type="submit"
+                disabled={isSubmitting}
+                className={`px-8 py-4 rounded-lg font-semibold text-white transition-all ${
+                  isSubmitting
+                    ? 'bg-gray-400 cursor-not-allowed'
+                    : 'bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700 transform hover:scale-105'
+                } focus:ring-4 focus:ring-purple-200`}
+              >
+                {isSubmitting ? (
+                  <div className="flex items-center space-x-2">
+                    <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white"></div>
+                    <span>Registering...</span>
+                  </div>
+                ) : (
+                  'Register Account'
+                )}
+              </button>
+            </div>
+          </form>
+          {/* ✅ form closed properly */}
+        </div>
+      </div>
+    </div>
+  );
+}

