@@ -0,0 +1,284 @@
+import React, { useState, useEffect } from 'react';
+import { 
+  Home, 
+  FileText, 
+  Users, 
+  Settings, 
+  Bell, 
+  Menu, 
+  User,
+  Clock,
+  Activity
+} from 'lucide-react';
+import { StatCard, UrgentAlertsCard, QuickActionsCard } from './C';
+import { ReportsTable } from './Table';
+import { Sidebar } from './Sidebar';
+
+const GBVAdminDashboard = () => {
+  const [activeSection, setActiveSection] = useState('dashboard');
+  const [sidebarOpen, setSidebarOpen] = useState(false);
+  const [urgentReports, setUrgentReports] = useState([]);
+  const [stats, setStats] = useState({
+    activeReports: 24,
+    pendingAssignments: 8,
+    avgResponseTime: '2.3h',
+    onlineResponders: 15
+  });
+
+  // Mock data
+  useEffect(() => {
+    setUrgentReports([
+      {
+        id: 'GBV-2024-001',
+        type: 'Domestic Violence',
+        location: 'Nairobi Central',
+        urgency: 'Critical',
+        timeAgo: '15 min ago',
+        status: 'Unassigned'
+      },
+      {
+        id: 'GBV-2024-002',
+        type: 'Sexual Harassment',
+        location: 'Westlands',
+        urgency: 'High',
+        timeAgo: '1h ago',
+        status: 'Assigned'
+      }
+    ]);
+  }, []);
+
+  const sidebarItems = [
+    { id: 'dashboard', label: 'Dashboard', icon: Home },
+    { id: 'reports', label: 'Reports', icon: FileText, badge: '24' },
+    { id: 'users', label: 'Users', icon: Users },
+    { id: 'settings', label: 'Settings', icon: Settings },
+  ];
+
+  const handleSectionChange = (section) => {
+    setActiveSection(section);
+    setSidebarOpen(false);
+  };
+
+  const handleAssignReport = (reportId) => {
+    console.log('Assigning report:', reportId);
+  };
+
+  const handleViewAllAlerts = () => {
+    setActiveSection('reports');
+  };
+
+  const handleNewReport = () => {
+    console.log('Creating new report');
+  };
+
+  const handleFilterClick = () => {
+    console.log('Opening filter');
+  };
+
+  // Header Component
+  const Header = () => (
+    <header className="bg-white shadow-sm border-b border-gray-200">
+      <div className="flex items-center justify-between h-16 px-6">
+        <div className="flex items-center">
+          <button
+            onClick={() => setSidebarOpen(true)}
+            className="lg:hidden text-gray-600 hover:text-gray-900"
+          >
+            <Menu className="w-6 h-6" />
+          </button>
+          <h1 className="ml-4 lg:ml-0 text-xl font-semibold text-gray-900">
+            {sidebarItems.find(item => item.id === activeSection)?.label || 'Dashboard'}
+          </h1>
+        </div>
+        
+        <div className="flex items-center space-x-4">
+          <button className="relative p-2 text-gray-600 hover:text-gray-900">
+            <Bell className="w-5 h-5" />
+            <span className="absolute top-0 right-0 w-2 h-2 bg-red-500 rounded-full"></span>
+          </button>
+          
+          <div className="flex items-center space-x-2">
+            <div className="w-8 h-8 bg-blue-600 rounded-full flex items-center justify-center">
+              <User className="w-4 h-4 text-white" />
+            </div>
+            <span className="hidden md:inline text-sm font-medium text-gray-700">Admin User</span>
+          </div>
+        </div>
+      </div>
+    </header>
+  );
+
+  // Dashboard Content
+  const DashboardContent = () => (
+    <div className="space-y-6">
+      {/* Stats Cards */}
+      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
+        <StatCard
+          title="Total Reports"
+          value={stats.activeReports}
+          icon={FileText}
+          color="text-blue-600"
+          trend="+12% this week"
+        />
+        <StatCard
+          title="Pending Cases"
+          value={stats.pendingAssignments}
+          icon={Clock}
+          color="text-orange-600"
+        />
+        <StatCard
+          title="In Progress"
+          value={stats.avgResponseTime}
+          icon={Activity}
+          color="text-green-600"
+          trend="-0.5h improved"
+        />
+        <StatCard
+          title="Resolved Cases"
+          value={stats.onlineResponders}
+          icon={Users}
+          color="text-purple-600"
+        />
+      </div>
+
+      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
+        {/* Urgent Alerts */}
+        <div className="lg:col-span-1">
+          <UrgentAlertsCard
+            urgentReports={urgentReports}
+            onAssign={handleAssignReport}
+            onViewAll={handleViewAllAlerts}
+          />
+        </div>
+
+        {/* Quick Actions & Activity Feed */}
+        <div className="lg:col-span-2">
+          <QuickActionsCard />
+        </div>
+      </div>
+    </div>
+  );
+
+  // User Management
+  const UserManagement = () => {
+    const [activeTab, setActiveTab] = useState('responders');
+
+    const tabs = [
+      { id: 'responders', label: 'Responders' },
+      { id: 'administrators', label: 'Administrators' },
+      { id: 'inactive', label: 'Inactive Users' }
+    ];
+
+    return (
+      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
+        <h2 className="text-2xl font-bold text-gray-900 mb-6">User Management</h2>
+        <div className="flex space-x-1 mb-6">
+          {tabs.map((tab) => (
+            <button
+              key={tab.id}
+              onClick={() => setActiveTab(tab.id)}
+              className={`px-4 py-2 text-sm font-medium rounded-lg transition-colors ${
+                activeTab === tab.id
+                  ? 'text-blue-600 bg-blue-50'
+                  : 'text-gray-600 hover:bg-gray-50 hover:text-gray-900'
+              }`}
+            >
+              {tab.label}
+            </button>
+          ))}
+        </div>
+        <p className="text-gray-600">
+          User management interface would be implemented here with role-based access controls and secure user data handling.
+        </p>
+      </div>
+    );
+  };
+
+  // System Settings
+  const SystemSettings = () => (
+    <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
+      <h2 className="text-2xl font-bold text-gray-900 mb-6">System Settings</h2>
+      <div className="space-y-6">
+        <div>
+          <h3 className="text-lg font-medium text-gray-900 mb-3">General Settings</h3>
+          <div className="space-y-3">
+            <div className="flex items-center justify-between">
+              <span className="text-gray-700">Platform Name</span>
+              <input 
+                type="text" 
+                value="GBV Reporting Platform" 
+                className="px-3 py-2 border border-gray-300 rounded-lg" 
+                readOnly 
+              />
+            </div>
+            <div className="flex items-center justify-between">
+              <span className="text-gray-700">Auto-logout Timer</span>
+              <select className="px-3 py-2 border border-gray-300 rounded-lg">
+                <option>15 minutes</option>
+                <option>30 minutes</option>
+                <option>1 hour</option>
+              </select>
+            </div>
+          </div>
+        </div>
+      </div>
+    </div>
+  );
+
+  const renderContent = () => {
+    switch (activeSection) {
+      case 'dashboard':
+        return <DashboardContent />;
+      case 'reports':
+        return (
+          <ReportsTable
+            onFilterClick={handleFilterClick}
+            onNewReport={handleNewReport}
+            onViewReport={(id) => console.log('Viewing report:', id)}
+            onAssignReport={handleAssignReport}
+          />
+        );
+      case 'users':
+        return <UserManagement />;
+      case 'settings':
+        return <SystemSettings />;
+      default:
+        return null;
+    }
+  };
+
+  return (
+    <div className="flex h-screen bg-gray-50">
+      {/* Sidebar */}
+      <Sidebar
+        sidebarItems={sidebarItems}
+        activeSection={activeSection}
+        sidebarOpen={sidebarOpen}
+        onSectionChange={handleSectionChange}
+        onSidebarClose={() => setSidebarOpen(false)}
+      />
+      
+      {/* Mobile overlay */}
+      {sidebarOpen && (
+        <div
+          className="fixed inset-0 z-40 bg-black bg-opacity-50 lg:hidden"
+          onClick={() => setSidebarOpen(false)}
+        />
+      )}
+
+      {/* Main Content */}
+      <div className="flex-1 flex flex-col overflow-hidden">
+        <Header />
+
+        {/* Main Content Area */}
+        <main className="flex-1 overflow-auto">
+          <div className="p-6">
+            {renderContent()}
+          </div>
+        </main>
+      </div>
+    </div>
+  );
+};
+
+export default GBVAdminDashboard;
